import React, { useState, useEffect } from "react";
import Navbar from "../components/Navbar";
import Posts from "../components/Posts";
import SuggestedPeople from "../components/SuggestedPeople";
import Communities from "../components/Communities";
import CreatePost from "../assets/components/CreatePost";
import '../assets/styles/Home.css';

function Home() {
    const [posts, setPosts] = useState([]);
    const [suggestedPeople, setSuggestedPeople] = useState([
        { id: 1, name: 'Helena Hills', username: 'helenahills', avatar: 'path/to/avatar.jpg' },
        // Add more people
    ]);

    const [communities, setCommunities] = useState([
        { id: 1, name: 'Design Enthusiasts', members: '13.2k' },
        // Add more communities
    ]);

    useEffect(() => {
        fetch('http://localhost:5000/api/posts')
            .then(response => response.json())
            .then(data => setPosts(data))
            .catch(error => console.error('Error fetching posts:', error));
    }, []);

    const addPost = (post) => {
        setPosts([post, ...posts]);
    };

    return (
        <div className="home">
            <div className="main-content">
                <Navbar />
                <CreatePost addPost={addPost} />
                <div className="posts">
                    {posts.map((post, index) => (
                        <Posts key={index} {...post} />
                    ))}
                </div>
                <div className="sidebar-right">
                    <SuggestedPeople people={suggestedPeople} />
                    <Communities communities={communities} />
                </div>
            </div>
        </div>
    );
}

export default Home;
