import React, { useState } from 'react';

const Posts = ({ id, user, group, time, image, description, likes, comments }) => {
    const [commentText, setCommentText] = useState('');
    const [postComments, setPostComments] = useState(comments);
    const [postLikes, setPostLikes] = useState(likes);

    const handleCommentSubmit = async (e) => {
        e.preventDefault();
        const token = localStorage.getItem('token');
        const response = await fetch(`http://localhost:5000/api/posts/${id}/comments`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${token}`
            },
            body: JSON.stringify({ content: commentText })
        });

        if (response.ok) {
            const newComment = await response.json();
            setPostComments([...postComments, newComment]);
            setCommentText('');
        } else {
            console.error('Error adding comment');
        }
    };

    const handleLike = async () => {
        const token = localStorage.getItem('token');
        const response = await fetch(`http://localhost:5000/api/posts/${id}/like`, {
            method: 'POST',
            headers: {
                'Authorization': `Bearer ${token}`
            }
        });

        if (response.ok) {
            setPostLikes(postLikes + 1);
        } else {
            console.error('Error liking post');
        }
    };

    return (
        <div className="post">
            <div className="post-header">
                <img src={user.avatar} alt={user.name} />
                <div>
                    <h4>{user.name}</h4>
                    <p>{group} - {time}</p>
                </div>
            </div>
            <img src={image} alt="Post image" />
            <p>{description}</p>
            <div className="post-actions">
                <button onClick={handleLike}>Like ({postLikes})</button>
            </div>
            <div className="post-comments">
                {postComments.map((comment, index) => (
                    <div key={index} className="comment">
                        <p><strong>{comment.user.name}</strong>: {comment.content}</p>
                    </div>
                ))}
                <form onSubmit={handleCommentSubmit}>
                    <input
                        type="text"
                        placeholder="Add a comment..."
                        value={commentText}
                        onChange={(e) => setCommentText(e.target.value)}
                    />
                    <button type="submit">Comment</button>
                </form>
            </div>
        </div>
    );
};

export default Posts;
